#![cfg(test)]

#[cfg(feature = "testnet")]
use artimonist::BIP44;
#[cfg(not(feature = "testnet"))]
use artimonist::BIP49;

use artimonist::{Error, Xpriv};
use std::str::FromStr;

/// # Reference
///   electrum-4.5.8-portable.exe --testnet
///   Select multisig wallet, import xpubs.
#[test]
fn multisig23() -> Result<(), Error> {
    use test_data_23::*;
    let master = Xpriv::from_str(MASTER_KEY)?;

    // electrum not support upriv, vpriv ...
    // so, testnet use bip44
    #[cfg(not(feature = "testnet"))]
    let accounts = (0..3)
        .map(|i| master.bip49_account(i))
        .collect::<Result<Vec<_>, _>>()?;
    #[cfg(feature = "testnet")]
    let accounts = (0..3)
        .map(|i| master.bip44_account(i))
        .collect::<Result<Vec<_>, _>>()?;

    for (i, account) in accounts.iter().enumerate() {
        assert_eq!(account.0, ACCOUNT_XPUBS[i]);
        assert_eq!(account.1, ACCOUNT_XPRVS[i]);
    }

    #[cfg(not(feature = "testnet"))]
    let addresses = (0..20)
        .map(|i| master.bip49_multisig::<2, 3>(0, i))
        .collect::<Result<Vec<_>, _>>()?;
    #[cfg(feature = "testnet")]
    let addresses = (0..20)
        .map(|i| master.bip44_multisig::<2, 3>(0, i))
        .collect::<Result<Vec<_>, _>>()?;

    for (i, address) in addresses.iter().enumerate() {
        assert_eq!(address.0, MULTISIG_ADDRS[i]);
        assert_eq!(address.1, MULTISIG_SCRIPTS[i]);
    }
    Ok(())
}

